const speed=.75;window.addEventListener("scroll",(function(){const e=window.scrollY;document.querySelectorAll(".section-title").forEach((o=>{o.style.transform=`translateY(${.25*e}px)`}))})),document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelectorAll(".section-title, body"),o=window.innerHeight;window.addEventListener("scroll",(function(){window.scrollY>o?e.forEach((e=>e.classList.add("scrolled"))):e.forEach((e=>e.classList.remove("scrolled")))}))}));